@app.route("/")
def home():
    return render_template("index.html", prediction=None, crop_image=None)

@app.route("/predict", methods=["POST"])
def predict():
    try:
        N = float(request.form["N"])
        P = float(request.form["P"])
        K = float(request.form["K"])
        temperature = float(request.form["temperature"])
        humidity = float(request.form["humidity"])
        ph = float(request.form["ph"])
        rainfall = float(request.form["rainfall"])

        data = np.array([[N, P, K, temperature, humidity, ph, rainfall]])
        scaled_data = scaler.transform(data)

        prediction = model.predict(scaled_data)[0]
        # Use the dictionary to get the online image URL
        crop_image = crop_images.get(prediction.lower().replace(" ", ""), None)
        return render_template("index.html", prediction=f"🌿 Recommended Crop: {prediction.capitalize()}", crop_image=crop_image)
    
    except:
        return render_template("index.html", prediction="❌ Please enter valid values!", crop_image=None)

if __name__ == "__main__":
    port = int(os.environ.get("PORT", 5000))
    app.run(host="0.0.0.0", port=port, debug=False)
